import React, { useState } from 'react';
import { useTheme } from '../context/ThemeContext';

export default function ThemeSelector() {
  const { currentTheme, themes, changeTheme } = useTheme();
  const [isOpen, setIsOpen] = useState(false);

  const handleThemeChange = (themeName) => {
    changeTheme(themeName);
    setIsOpen(false);
    console.log('Theme changed to:', themeName); // Debug log
  };

  return (
    <div className="fixed top-4 right-4 z-50">
      <div className="relative">
        <button 
          className="px-4 py-2 bg-black/20 backdrop-blur-md border border-white/20 rounded-lg text-white font-medium hover:bg-black/30 transition-all"
          onClick={() => setIsOpen(!isOpen)}
        >
          {themes[currentTheme]?.name || 'Default'} Theme
        </button>
        
        {isOpen && (
          <div className="absolute right-0 top-full mt-2 w-48 bg-black/40 backdrop-blur-xl border border-white/20 rounded-lg shadow-2xl">
            <div className="p-2 space-y-1">
              {Object.entries(themes).map(([key, theme]) => (
                <button
                  key={key}
                  onClick={() => handleThemeChange(key)}
                  className={`w-full text-left px-3 py-2 rounded-md text-sm transition-all ${
                    currentTheme === key
                      ? 'bg-white/20 text-white font-medium'
                      : 'text-gray-300 hover:bg-white/10 hover:text-white'
                  }`}
                >
                  {theme.name}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
