import React, { useState } from 'react';
import { mockNhost } from '../graphql';

export default function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    const { error: authError } = isLogin 
      ? await mockNhost.auth.signIn({ email, password })
      : await mockNhost.auth.signUp({ email, password });
    
    setLoading(false);
    if (authError) {
      setError(authError.message);
    } else {
      onLogin();
    }
  };

  return (
    // Further enhanced high-contrast background
    <div className="flex items-center justify-center min-h-screen bg-slate-200 dark:bg-gray-950">
      <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-2xl shadow-2xl dark:bg-slate-900 border-2 border-slate-200 dark:border-slate-700">
        <h2 className="text-3xl font-bold text-center text-black dark:text-white">
          {isLogin ? 'Welcome Back' : 'Create Account'}
        </h2>
        <form className="space-y-6" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="email" className="text-sm font-medium text-slate-700 dark:text-slate-300">
              Email address
            </label>
            <input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              // Further enhanced high-contrast input fields with cyan focus
              className="w-full px-4 py-2 mt-1 text-slate-900 bg-white border-2 border-slate-400 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-cyan-500 focus:border-cyan-500 dark:bg-slate-800 dark:border-slate-600 dark:text-white dark:placeholder-slate-400"
            />
          </div>
          <div>
            <label htmlFor="password" className="text-sm font-medium text-slate-700 dark:text-slate-300">
              Password
            </label>
            <input
              id="password"
              name="password"
              type="password"
              autoComplete={isLogin ? "current-password" : "new-password"}
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              // Further enhanced high-contrast input fields with cyan focus
              className="w-full px-4 py-2 mt-1 text-slate-900 bg-white border-2 border-slate-400 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-cyan-500 focus:border-cyan-500 dark:bg-slate-800 dark:border-slate-600 dark:text-white dark:placeholder-slate-400"
            />
          </div>
          {error && <p className="text-sm font-semibold text-red-600 dark:text-red-500">{error}</p>}
          <div>
            <button
              type="submit"
              disabled={loading}
              className="w-full px-4 py-3 text-base font-bold text-white bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700 border border-transparent rounded-lg shadow-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-cyan-400 disabled:opacity-50 transition-all transform hover:scale-105"
            >
              {loading ? 'Processing...' : (isLogin ? 'Sign In' : 'Sign Up')}
            </button>
          </div>
        </form>
        <p className="text-sm text-center text-slate-600 dark:text-slate-400">
          {isLogin ? "Don't have an account?" : 'Already have an account?'}
          {/* Enhanced high-contrast link with cyan color */}
          <button onClick={() => setIsLogin(!isLogin)} className="ml-1 font-bold text-cyan-600 hover:text-cyan-500 dark:text-cyan-400 dark:hover:text-cyan-300">
            {isLogin ? 'Sign Up' : 'Sign In'}
          </button>
        </p>
      </div>
    </div>
  );
}
