import React, { useState, useEffect, useRef } from 'react';
import { mockNhost } from '../graphql';

// Simple Shape components for the background animation
const Shape = ({ className, 'data-depth': dataDepth }) => <div data-depth={dataDepth} className={`absolute block bg-white/10 rounded-full ${className}`} />;

export default function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const sceneEl = useRef(null);

  useEffect(() => {
    // Dynamically import ParallaxJS to avoid server-side rendering issues
    import('parallax-js').then(ParallaxModule => {
      const Parallax = ParallaxModule.default;
      if (sceneEl.current) {
        const parallaxInstance = new Parallax(sceneEl.current, {
          relativeInput: true,
          hoverOnly: true,
          scalarX: 8,
          scalarY: 8,
        });

        // Cleanup on component unmount
        return () => parallaxInstance.destroy();
      }
    });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    const { error: authError } = isLogin 
      ? await mockNhost.auth.signIn({ email, password })
      : await mockNhost.auth.signUp({ email, password });
    
    setLoading(false);
    if (authError) {
      setError(authError.message);
    } else {
      onLogin();
    }
  };

  return (
    // Main container with animated gradient background
    <div className="relative flex items-center justify-center min-h-screen overflow-hidden bg-gradient-to-br from-slate-900 via-indigo-900 to-slate-900">
      
      {/* Parallax Shapes Container */}
      <div ref={sceneEl} className="absolute inset-0 w-full h-full z-0">
        <Shape data-depth="0.2" className="w-48 h-48 top-1/4 left-1/4 animate-blob" />
        <Shape data-depth="0.4" className="w-24 h-24 top-1/2 left-2/3 animate-blob animation-delay-2000" />
        <Shape data-depth="0.3" className="w-36 h-36 bottom-1/4 right-1/4 animate-blob animation-delay-4000" />
        <Shape data-depth="0.5" className="w-20 h-20 bottom-10 left-10 animate-blob animation-delay-1000" />
        <Shape data-depth="0.2" className="w-28 h-28 top-10 right-10 animate-blob animation-delay-3000" />
      </div>

      {/* 3D Tilt Form Card Container */}
      <div className="group [perspective:1000px]">
        <div className="relative z-10 w-full max-w-md p-8 space-y-6 bg-black/20 backdrop-blur-xl rounded-2xl shadow-2xl border border-white/20 transition-transform duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(10deg)_rotateX(5deg)]">
          <h2 className="text-4xl font-bold text-center text-white [text-shadow:0_0_15px_rgba(255,255,255,0.5)]">
            {isLogin ? 'Secure Login' : 'Join Us'}
          </h2>
          <form className="space-y-6" onSubmit={handleSubmit}>
            <div>
              <label htmlFor="email" className="text-sm font-medium text-slate-200">
                Email address
              </label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-3 mt-1 text-white bg-black/30 border-2 border-white/20 rounded-lg shadow-inner focus:outline-none focus:ring-2 focus:ring-fuchsia-500 focus:border-fuchsia-500 placeholder:text-slate-400 transition-all"
              />
            </div>
            <div>
              <label htmlFor="password" className="text-sm font-medium text-slate-200">
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete={isLogin ? "current-password" : "new-password"}
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 mt-1 text-white bg-black/30 border-2 border-white/20 rounded-lg shadow-inner focus:outline-none focus:ring-2 focus:ring-fuchsia-500 focus:border-fuchsia-500 placeholder:text-slate-400 transition-all"
              />
            </div>
            {error && <p className="text-sm font-semibold text-red-400">{error}</p>}
            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full px-4 py-3 text-lg font-bold text-white bg-gradient-to-r from-indigo-600 to-fuchsia-600 hover:from-indigo-700 hover:to-fuchsia-700 border border-transparent rounded-lg shadow-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-900 focus:ring-fuchsia-500 disabled:opacity-50 transition-all transform hover:scale-105 hover:shadow-fuchsia-500/50"
              >
                {loading ? 'Authenticating...' : (isLogin ? 'Sign In' : 'Create Account')}
              </button>
            </div>
          </form>
          <p className="text-sm text-center text-slate-400">
            {isLogin ? "Don't have an account?" : 'Already have an account?'}
            <button onClick={() => setIsLogin(!isLogin)} className="ml-1 font-bold text-fuchsia-400 hover:text-fuchsia-300 hover:underline transition-colors">
              {isLogin ? 'Sign Up' : 'Sign In'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}
