import React, { useState, useEffect, useCallback } from 'react';
import { mockNhost } from '../graphql';
import { GQL } from '../graphql';
import ChatList from '../components/ChatList';
import MessageView from '../components/MessageView';

// Background Glow component for reuse
const Glow = ({ className }) => <div className={`absolute rounded-full mix-blend-lighten filter blur-3xl opacity-50 animate-blob ${className}`}></div>;

export default function ChatPage({ client, onLogout }) {
    const [chats, setChats] = useState([]);
    const [activeChatId, setActiveChatId] = useState(null);
    const user = mockNhost.auth.getUser();

    const fetchChats = useCallback(async () => {
        const { data } = await client.query(GQL.GET_CHATS_QUERY, { user_id: user.id });
        if (data && data.chats) {
            setChats(data.chats);
            if (!activeChatId && data.chats.length > 0) {
                setActiveChatId(data.chats[0].id);
            }
        }
    }, [client, user.id, activeChatId]);

    useEffect(() => {
        fetchChats();
    }, [fetchChats]);

    const handleCreateChat = async () => {
        const { data } = await client.mutation(GQL.CREATE_CHAT_MUTATION, { user_id: user.id });
        if (data && data.insert_chats_one) {
            const newChat = data.insert_chats_one;
            setChats(prev => [newChat, ...prev]);
            setActiveChatId(newChat.id);
        }
    };

    return (
        <div className="relative h-screen w-screen flex font-sans text-white bg-black overflow-hidden">
            {/* Animated Background Glows */}
            <Glow className="top-0 -left-4 w-96 h-96 bg-cyan-700" />
            <Glow className="top-0 -right-4 w-96 h-96 bg-fuchsia-700 animation-delay-2000" />
            <Glow className="bottom-0 -left-20 w-96 h-96 bg-sky-700 animation-delay-4000" />

            {/* Main Content */}
            <div className="relative z-10 w-full md:w-1/3 lg:w-1/4 xl:w-1/5 h-full">
                <ChatList
                    chats={chats}
                    onSelectChat={setActiveChatId}
                    onCreateChat={handleCreateChat}
                    activeChatId={activeChatId}
                    onLogout={onLogout}
                />
            </div>
            <div className="relative z-10 hidden md:flex flex-col flex-grow h-full">
                <MessageView chatId={activeChatId} client={client} />
            </div>
        </div>
    );
}
