import React, { useState } from 'react';
import { mockNhost } from '../graphql';
import { useTheme } from '../context/ThemeContext';

// Simple Shape components for the background animation
const Shape = ({ className, color }) => <div className={`absolute block rounded-full ${className}`} style={{ backgroundColor: color }} />;

export default function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { currentThemeData } = useTheme();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    const { error: authError } = isLogin 
      ? await mockNhost.auth.signIn({ email, password })
      : await mockNhost.auth.signUp({ email, password });
    
    setLoading(false);
    if (authError) {
      setError(authError.message);
    } else {
      onLogin();
    }
  };

  return (
    // Main container with dynamic theme background
    <div className="relative flex items-center justify-center min-h-screen overflow-hidden" 
         style={{ background: `linear-gradient(-45deg, ${currentThemeData.colors.background}, ${currentThemeData.colors.surface}, ${currentThemeData.colors.primary}, ${currentThemeData.colors.background})` }}>
      
      {/* Animated Shapes with theme colors */}
      <div className="absolute inset-0 w-full h-full z-0">
        <Shape className="w-48 h-48 top-1/4 left-1/4 animate-pulse" color={`${currentThemeData.colors.primary}20`} />
        <Shape className="w-24 h-24 top-1/2 left-2/3 animate-pulse animation-delay-1000" color={`${currentThemeData.colors.secondary}20`} />
        <Shape className="w-36 h-36 bottom-1/4 right-1/4 animate-pulse animation-delay-2000" color={`${currentThemeData.colors.accent}20`} />
        <Shape className="w-20 h-20 bottom-10 left-10 animate-pulse animation-delay-3000" color={`${currentThemeData.colors.primary}20`} />
        <Shape className="w-28 h-28 top-10 right-10 animate-pulse animation-delay-500" color={`${currentThemeData.colors.secondary}20`} />
      </div>

      {/* 3D Tilt Form Card Container */}
      <div className="group [perspective:1000px]">
        <div className="relative z-10 w-full max-w-md p-8 space-y-6 bg-black/20 backdrop-blur-xl rounded-2xl shadow-2xl border border-white/20 transition-transform duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(10deg)_rotateX(5deg)]">
          <h2 className="text-4xl font-bold text-center text-white [text-shadow:0_0_15px_rgba(255,255,255,0.5)]">
            {isLogin ? 'Secure Login' : 'Join Us'}
          </h2>
          <form className="space-y-6" onSubmit={handleSubmit}>
            <div>
              <label htmlFor="email" className="text-sm font-medium text-slate-200">
                Email address
              </label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-3 mt-1 text-white bg-black/30 border-2 rounded-lg shadow-inner focus:outline-none focus:ring-2 placeholder:text-slate-400 transition-all"
                style={{ 
                  borderColor: currentThemeData.colors.primary,
                  '--tw-ring-color': currentThemeData.colors.primary
                }}
              />
            </div>
            <div>
              <label htmlFor="password" className="text-sm font-medium text-slate-200">
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete={isLogin ? "current-password" : "new-password"}
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 mt-1 text-white bg-black/30 border-2 rounded-lg shadow-inner focus:outline-none focus:ring-2 placeholder:text-slate-400 transition-all"
                style={{ 
                  borderColor: currentThemeData.colors.primary,
                  '--tw-ring-color': currentThemeData.colors.primary
                }}
              />
            </div>
            {error && <p className="text-sm font-semibold text-red-400">{error}</p>}
            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full px-4 py-3 text-lg font-bold text-white border border-transparent rounded-lg shadow-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-900 disabled:opacity-50 transition-all transform hover:scale-105"
                style={{
                  background: `linear-gradient(135deg, ${currentThemeData.colors.primary}, ${currentThemeData.colors.secondary})`
                }}
              >
                {loading ? 'Authenticating...' : (isLogin ? 'Sign In' : 'Create Account')}
              </button>
            </div>
          </form>
          <p className="text-sm text-center text-slate-400">
            {isLogin ? "Don't have an account?" : 'Already have an account?'}
            <button 
              onClick={() => setIsLogin(!isLogin)} 
              className="ml-1 font-bold hover:underline transition-colors"
              style={{ color: currentThemeData.colors.accent }}
            >
              {isLogin ? 'Sign Up' : 'Sign In'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}
