import React, { createContext, useContext, useState } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const ThemeProvider = ({ children }) => {
  const [currentTheme, setCurrentTheme] = useState('default');

  const themes = {
    default: {
      name: 'Default',
      colors: {
        primary: '#3b82f6',
        secondary: '#8b5cf6',
        accent: '#06b6d4',
        background: '#0f172a',
        surface: '#1e293b',
        text: '#f8fafc',
        border: '#334155'
      },
      gradients: {
        primary: 'from-blue-600 to-indigo-600',
        secondary: 'from-purple-600 to-pink-600',
        background: 'from-slate-900 via-blue-900 to-slate-900'
      }
    },
    ocean: {
      name: 'Ocean',
      colors: {
        primary: '#0891b2',
        secondary: '#0ea5e9',
        accent: '#22d3ee',
        background: '#0c4a6e',
        surface: '#0e7490',
        text: '#f0f9ff',
        border: '#0284c7'
      },
      gradients: {
        primary: 'from-cyan-600 to-blue-600',
        secondary: 'from-blue-600 to-sky-600',
        background: 'from-blue-950 via-cyan-900 to-blue-950'
      }
    },
    sunset: {
      name: 'Sunset',
      colors: {
        primary: '#f97316',
        secondary: '#ec4899',
        accent: '#f59e0b',
        background: '#7c2d12',
        surface: '#9a3412',
        text: '#fef3c7',
        border: '#ea580c'
      },
      gradients: {
        primary: 'from-orange-500 to-red-500',
        secondary: 'from-pink-500 to-rose-500',
        background: 'from-orange-950 via-red-900 to-orange-950'
      }
    },
    forest: {
      name: 'Forest',
      colors: {
        primary: '#16a34a',
        secondary: '#059669',
        accent: '#10b981',
        background: '#14532d',
        surface: '#166534',
        text: '#f0fdf4',
        border: '#15803d'
      },
      gradients: {
        primary: 'from-green-600 to-emerald-600',
        secondary: 'from-emerald-600 to-teal-600',
        background: 'from-green-950 via-emerald-900 to-green-950'
      }
    },
    cyberpunk: {
      name: 'Cyberpunk',
      colors: {
        primary: '#a855f7',
        secondary: '#ec4899',
        accent: '#06b6d4',
        background: '#1e1b4b',
        surface: '#312e81',
        text: '#faf5ff',
        border: '#7c3aed'
      },
      gradients: {
        primary: 'from-purple-600 to-pink-600',
        secondary: 'from-pink-600 to-cyan-600',
        background: 'from-indigo-950 via-purple-900 to-indigo-950'
      }
    }
  };

  const changeTheme = (themeName) => {
    console.log('Changing theme from', currentTheme, 'to', themeName);
    setCurrentTheme(themeName);
  };

  const value = {
    currentTheme,
    themes,
    currentThemeData: themes[currentTheme],
    changeTheme
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
};
